import React from 'react';
import useAuth from './../../Hooks/useAuth';
import { Route, Redirect } from 'react-router';

const PrivateRoute = ({ children, ...rest }) => {
    const { users } = useAuth()
    return (
        <Route {...rest}
            render={({ location }) =>
                users.email ? (
                    children
                ) : (
                    <Redirect></Redirect>
                )
            }>

        </Route>
    );
};

export default PrivateRoute;
